{% set alle_trainings = frappe.get_all('Teilnehmer', filters={'user': doc.spieler}, fields=['parent']) %}
{% set betroffene_spieler = [] %}
{% for _training in alle_trainings %}
	{% set training = frappe.get_doc("DP Event", _training.parent) %}
	{% if frappe.utils.get_datetime(training.start) >= frappe.utils.get_datetime(doc.datum) %}
		{% for spieler in training.anmeldungen %}
			{% if (spieler.user != doc.spieler) and (spieler.user not in betroffene_spieler) %}
				{% if betroffene_spieler.append(spieler.user) %}{% endif %}
			{% endif %}
		{% endfor %}
	{% endif %}
{% endfor %}
<h1>Covid-19 Tracing</h1>
<h2>Betroffener Spieler: {{ frappe.get_doc("DP User", doc.spieler).fullname }}, Stichtag: {{ frappe.utils.get_datetime(doc.datum).strftime('%d.%m.%Y') }}</h2>
<p>Folgende Spieler kamen nach dem genannten Stichtag mit {{ doc.spieler }} im Training in Kontakt:</p>
<hr>
{% for _spieler in betroffene_spieler %}
	{% set spieler = frappe.get_doc("DP User", _spieler) %}
	<p>{{ spieler.fullname }}<br>
	{{ spieler.strasse }}, {{ spieler.plz }} {{ spieler.ort }}<br>
	{{ spieler.telefon }}</p><hr>
{% endfor %}
